class Solution(object):
    def highestPeak(self, isWater):
        """
        :type isWater: List[List[int]]
        :rtype: List[List[int]]
        """
        m = len(isWater)  # rows
        n = len(isWater[0])  # columns
        height = [[float('inf') for _ in range(n)] for _ in range(m)]
        queue = []
        for i in range(m):
            for j in range(n):
                if isWater[i][j] == 1:
                    queue.append((i, j))
                    height[i][j] = 0
        visited = {}
        while len(queue) > 0:
            x, y = queue.pop(0)
            # print(len(queue), len(visited), len(set(visited)), n*m, m,n, x,y)
            if height[x][y] == float('inf'):
                temp = []
                if x + 1 < m:
                    temp.append(height[x + 1][y])
                if x - 1 >= 0:
                    temp.append(height[x - 1][y])
                if y + 1 < n:
                    temp.append(height[x][y + 1])
                if y - 1 >= 0:
                    temp.append(height[x][y - 1])
                height[x][y] = min(temp) + 1
            visited[(x, y)] = 1
            if x + 1 < m and (x+1, y) not in visited:
                queue.append((x+1, y))
                visited[(x+1, y)] = 1
            if x - 1 >= 0 and (x-1, y) not in visited:
                queue.append((x-1, y))
                visited[(x-1, y)] = 1
            if y + 1 < n and (x, y+1) not in visited:
                queue.append((x, y+1))
                visited[(x, y+1)] = 1
            if y - 1 >= 0 and (x, y-1) not in visited:
                queue.append((x, y-1))
                visited[(x, y-1)] = 1
        return height


